def get_gender(name):
    """
    arguments:
        name (string) - full or first name

    returns string with a given gender or None if haven't found out
    raises ValueError if `name` is not a string or if it's empty string
    """
    if type(name) is not str or not name.strip():
        raise ValueError("`name` must be a string containing a name")

    if name.endswith("á"):
        return "female"

    else:
        first_name = name.split()[0]
        first_name = first_name.capitalize()

        if first_name in male_names:
            return "male"

        elif first_name in female_names:
            return "female"

        return None

